import { Injectable } from '@angular/core';
import {WebRequestService} from './web-request.service';

@Injectable({
  providedIn: 'root'
})
export class TaskService {

  constructor(private webReqService: WebRequestService) { }

  createList(title: string){
    //we want to send a web request to create a list
    return this.webReqService.post('lists', {title});
   }
  getLists(){
    return this.webReqService.get('lists');
  }

  getTasks(listId: string) {
    return this.webReqService.get(`lists/${listId}/tasks`);
  }
  createTask(title: string, listId: string) {
    // Send a web request to create a task
    console.log("vachindi", title);
    this.webReqService.post(`lists/${listId}/tasks`, { title })
      .subscribe(
        (response: any) => {
          // Handle the response here
          console.log("Response:", response);
          // You can perform further operations with the response data
  
          return response; // Optionally, you can return the response if needed
        },
        (error: any) => {
          // Handle the error if the request fails
          console.error("Error:", error);
        }
      );
  }
  
}
